perspectives.style:
//USE THIS TO SHOW THEME AND FILTER STYLE ONLY. 

// This View uses the ALL TAGS (uncleaned) dataset with FILTER on all tags.


@settings {
  template: stakeholder;
  element-scale: scale("degree", 0.75, 4.5);
  text-overflow: clip 35;
  background-color: #f5f2ef;
  //flag: "Core Tags" with paired;
  //layout-gravity: 0.00001;
  //layout-particle-charge: 300;
  //connection-length: 500;
  //connection-strength: 0.1;
}

@controls {

   top {
    title {
      value: "UBC Climate Asset Map";
    }
  }

   top {
    filter {
      target: element;
      as: label;
      multiple: true;

      option {
        label: "Groups";
        selector: group;
        default: true;
      }

      option {
        label: "People";
        selector: person;
        default: true;
      }

      option {
        label: "Projects";
        selector: project;
        default: true;
      }
    }
  }

  bottom-right {
    filter {
      target: element;
      by: "Tags";
      as: dropdown;
      multiple: false;
      default: show-all;
      label: "Tag #1";
      placeholder: "Select";
    }
  }

  bottom-right {
    filter {
      target: element;
      by: "Tags";
      as: dropdown;
      multiple: false;
      default: show-all;
      label: "Tag #2";
      placeholder: "Select";
    }
  }

  bottom-right {
    filter {
      target: element;
      by: "Tags";
      as: dropdown;
      multiple: false;
      default: show-all;
      label: "Tag #3";
      placeholder: "Select";
    }
  }
}

element {
  popover: "### {{label}}  ---  {{Tags}}";
}

/* elements: Group */
group {
  icon: tree;
  icon-color: #8d3;
  color: transparent;
  shadow-color: #8d3;
  shadow-size: 1;
  flag: none;
}

/* elements: Person */
person {
  icon: user-tie;
  icon-color: #444;
  color: transparent;
  shadow-color: #444;
  shadow-size: 1;
  flag: none;
}

/* elements: Project */
project {
  icon: seedling;
  icon-color: #5b3;
  //border-color: #444;
  //border-opacity: 1;
  //border-width: 2;
  shadow-color: #5b3;
  shadow-size: 1;
  color: transparent;
}

@controls {
  top {
    title {
      value: "UBC Climate Asset Map";
