      default: show-all;
      label: "Tag #1";
      placeholder: "Select";
      color: #fff;
    }

    filter {
      target: element;
      by: "Tags";
      as: dropdown;
      multiple: false;
      default: show-all;
      label: "Tag #2";
      placeholder: "Select";
      color: #fff;
    }

    filter {
      target: element;
